import { Serie } from '@youwol/dataframe';
/**
 * Add several Series to generate a new one
 * @example
 * ```ts
 * // perform: a = b + c
 * const a = add([
 *     df.get('b'),
 *     df.get('c'),
 * ])
 * ```
 * @example
 * ```ts
 * // perform: a = 0.1*b + 0.3*c + 0.7*d
 * const a = add([
 *     mult( df.get('b'), 0.1),
 *     mult( df.get('c'), 0.3),
 *     mult( df.get('d'), 0.7)
 * ])
 * ```
 * @category Dataframe
 */
export declare const add: (series: Serie[]) => Serie<import("@youwol/dataframe").IArray>;
/**
 * Add a number to each value of the array
 * @example
 * ```ts
 * const a = addNumber( df.get('a'), 10 )
 * ```
 * @category Dataframe
 */
export declare const addNumber: (serie: Serie, a: number) => Serie<import("@youwol/dataframe").IArray>;
