import { Serie } from '@youwol/dataframe'
import { normals } from "./normals"
import { div } from "./div"
import { norm } from "./norm"

/**
 * @category Dataframe
 */
export const areas = (positions: Serie, indices: Serie): Serie => {
    if (!positions || !indices) {
        return undefined
    }
    const ns = normals(positions, indices)
    return ns === undefined ? undefined : div(norm(ns), 2)
}
